import React from 'react';
import { NavLink } from 'react-router-dom';
import { Plus, List, Package } from 'phosphor-react';

const BottomTabBar: React.FC = () => {
  const tabs = [
    {
      to: '/add',
      label: 'Digitalize',
      icon: Plus,
      activeColor: 'text-accent'
    },
    {
      to: '/inventory',
      label: 'Inventory',
      icon: List,
      activeColor: 'text-accent'
    },
    {
      to: '/skus',
      label: 'Catalog',
      icon: Package,
      activeColor: 'text-accent'
    }
  ];

  return (
    <nav style={{position: 'fixed', bottom: 0, left: 0, right: 0, backgroundColor: 'white', borderTop: '1px solid #ccc', zIndex: 100}}>
      <div style={{maxWidth: '800px', margin: '0 auto', padding: '0'}}>
        <div style={{display: 'flex', justifyContent: 'space-around', padding: '8px 0'}}>
          {tabs.map((tab) => (
            <NavLink
              key={tab.to}
              to={tab.to}
              className={({ isActive }) =>
                `flex flex-col items-center py-2 px-4 min-h-[44px] transition-colors ${
                  isActive
                    ? `${tab.activeColor} font-medium`
                    : 'text-gray-medium hover:text-primary'
                }`
              }
            >
              {({ isActive }) => (
                <>
                  <tab.icon 
                    size={20} 
                    weight={isActive ? 'fill' : 'regular'} 
                  />
                  <span className="text-xs mt-1">{tab.label}</span>
                </>
              )}
            </NavLink>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default BottomTabBar;
