import React, { useState, useRef, useEffect } from 'react';
import { Microphone, Stop, Play, X } from 'phosphor-react';

interface VoiceRecorderProps {
  onRecordingComplete: (audioBlob: Blob) => void;
  onCancel: () => void;
}

type RecordingState = 'idle' | 'recording' | 'stopped' | 'playing';

const VoiceRecorder: React.FC<VoiceRecorderProps> = ({ onRecordingComplete, onCancel }) => {
  const [recordingState, setRecordingState] = useState<RecordingState>('idle');
  const [duration, setDuration] = useState(0);
  const [audioURL, setAudioURL] = useState<string>('');

  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  const MAX_DURATION = 120; // 2 minutes in seconds

  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
      if (audioURL) {
        URL.revokeObjectURL(audioURL);
      }
    };
  }, [audioURL]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          sampleRate: 44100
        } 
      });

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });

      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const url = URL.createObjectURL(audioBlob);
        setAudioURL(url);

        // Stop all tracks to free up the microphone
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      setRecordingState('recording');
      setDuration(0);

      // Start timer
      timerRef.current = setInterval(() => {
        setDuration(prev => {
          if (prev >= MAX_DURATION) {
            stopRecording();
            return prev;
          }
          return prev + 1;
        });
      }, 1000);

    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Unable to access microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && recordingState === 'recording') {
      mediaRecorderRef.current.stop();
      setRecordingState('stopped');

      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
  };

  const playRecording = () => {
    if (audioURL && audioRef.current) {
      audioRef.current.play();
      setRecordingState('playing');
    }
  };

  const handleAudioEnded = () => {
    setRecordingState('stopped');
  };

  const restartRecording = () => {
    if (audioURL) {
      URL.revokeObjectURL(audioURL);
      setAudioURL('');
    }
    setDuration(0);
    setRecordingState('idle');
  };

  const handleComplete = () => {
    if (audioChunksRef.current.length > 0) {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
      onRecordingComplete(audioBlob);
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getButtonContent = () => {
    switch (recordingState) {
      case 'recording':
        return <Stop size={48} color="white" weight="fill" />;
      case 'stopped':
        return <Play size={48} color="white" weight="fill" />;
      case 'playing':
        return <Microphone size={48} color="white" weight="fill" />;
      default:
        return <Microphone size={48} color="white" weight="fill" />;
    }
  };

  const getButtonAction = () => {
    switch (recordingState) {
      case 'idle':
        return startRecording;
      case 'recording':
        return stopRecording;
      case 'stopped':
      case 'playing':
        return playRecording;
      default:
        return startRecording;
    }
  };

  const isRecording = recordingState === 'recording';
  const hasRecording = recordingState === 'stopped' || recordingState === 'playing';

  return (
    <div className="flex flex-col items-center gap-6">
      {/* Microphone Button */}
      <div className="relative">
        <button
          onClick={getButtonAction}
          style={{
            width: '128px',
            height: '128px',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: isRecording ? '#EF4444' : '#7C3AED',
            border: 'none',
            cursor: 'pointer',
            transition: 'all 0.2s'
          }}
          className={`transition-all duration-200 transform hover:scale-105 ${
            isRecording
              ? 'bg-red-500 hover:bg-red-600 animate-pulse'
              : 'bg-accent hover:bg-purple-600'
          }`}
          disabled={recordingState === 'playing'}
        >
          {getButtonContent()}
        </button>

        {/* Recording Animation */}
        {isRecording && (
          <>
            <div className="absolute inset-0 rounded-full border-4 border-accent opacity-30 animate-ping"></div>
            <div className="absolute inset-0 rounded-full border-2 border-accent opacity-20 animate-ping animation-delay-75"></div>
          </>
        )}
      </div>

      {/* Timer Display */}
      <div className="text-center">
        <div className={`text-3xl font-mono font-bold ${isRecording ? 'text-red-500' : 'text-primary'}`}>
          {formatTime(duration)}
        </div>
        <p className="text-sm text-gray-medium mt-1">
          {isRecording ? 'Recording...' : hasRecording ? 'Tap to play' : 'Tap to start recording'}
        </p>
        {duration > 0 && (
          <div className="w-64 bg-gray-light rounded-full h-2 mt-2">
            <div
              className={`h-2 rounded-full transition-all duration-300 ${
                isRecording ? 'bg-red-500' : 'bg-accent'
              }`}
              style={{ width: `${(duration / MAX_DURATION) * 100}%` }}
            ></div>
          </div>
        )}
      </div>

      {/* Controls */}
      {(isRecording || hasRecording) && (
        <div className="flex gap-4">
          {isRecording && (
            <button
              onClick={stopRecording}
              className="btn-primary flex items-center gap-2"
            >
              <Stop size={16} />
              Stop Recording
            </button>
          )}

          {hasRecording && (
            <>
              <button
                onClick={restartRecording}
                className="btn-secondary flex items-center gap-2"
              >
                <X size={16} />
                Start Over
              </button>
              <button
                onClick={handleComplete}
                className="btn-primary flex items-center gap-2"
              >
                Continue
              </button>
            </>
          )}
        </div>
      )}

      {/* Cancel Button */}
      <button
        onClick={onCancel}
        className="text-gray-medium hover:text-primary transition-colors text-sm"
      >
        Cancel
      </button>

      {/* Hidden Audio Element */}
      {audioURL && (
        <audio
          ref={audioRef}
          src={audioURL}
          onEnded={handleAudioEnded}
          className="hidden"
        />
      )}

      {/* Instructions */}
      {recordingState === 'idle' && (
        <div className="text-center max-w-sm">
          <h3 className="font-semibold text-primary mb-2">Recording Tips:</h3>
          <ul className="text-sm text-gray-medium space-y-1 text-left">
            <li>• Speak clearly and mention brand and model</li>
            <li>• Describe the physical condition</li>
            <li>• Include serial numbers if visible</li>
            <li>• Mention any accessories included</li>
            <li>• Maximum recording time: 2 minutes</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default VoiceRecorder;
