import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../utils/api';
import VoiceRecorder from '../components/VoiceRecorder';
import ProcessingModal from '../components/ProcessingModal';
import EquipmentForm from '../components/EquipmentForm';

type PageState = 'recording' | 'processing' | 'form';

const AddItemPage: React.FC = () => {
  const [pageState, setPageState] = useState<PageState>('recording');
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const [extractedData, setExtractedData] = useState<any>(null);
  const [confidenceScores, setConfidenceScores] = useState<any>(null);
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  const handleRecordingComplete = (blob: Blob) => {
    setAudioBlob(blob);
    setPageState('processing');
  };

  const handleProcessingComplete = (data: any, confidence?: any) => {
    setExtractedData(data);
    setConfidenceScores(confidence);
    setPageState('form');
  };

  const handleProcessingCancel = () => {
    setPageState('recording');
    setAudioBlob(null);
  };

  const handleFormSubmit = async (formData: any) => {
    setSaving(true);
    try {
      const { user } = useAuth();
      if (!user) {
        throw new Error('User not authenticated');
      }

      // Simulate saving delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Store in localStorage for demo (in real app this would be API call)
      const existingItems = JSON.parse(localStorage.getItem('inventory') || '[]');
      const newItem = {
        id: Date.now().toString(),
        ...formData,
        created_by: user.uid,
        created_at: new Date().toISOString()
      };

      existingItems.push(newItem);
      localStorage.setItem('inventory', JSON.stringify(existingItems));

      console.log('Equipment saved successfully:', newItem);

      // Navigate to inventory page after successful save
      navigate('/inventory');
    } catch (error) {
      console.error('Error saving equipment:', error);
      alert('Failed to save equipment. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  const handleFormCancel = () => {
    setPageState('recording');
    setAudioBlob(null);
    setExtractedData(null);
  };

  if (pageState === 'form') {
    return (
      <div className="p-6">
        <div className="text-center mb-8">
          <h2 className="font-heading font-bold text-primary text-2xl mb-2">
            Review & Save Equipment
          </h2>
          <p className="text-gray-medium">
            Verify details and add to digital inventory
          </p>
        </div>

        <EquipmentForm
          initialData={extractedData}
          confidenceScores={extractedData?.confidence_scores}
          onSubmit={handleFormSubmit}
          onCancel={handleFormCancel}
          loading={saving}
        />
      </div>
    );
  }

  return (
    <>
      <div style={{padding: '0', minHeight: '400px'}}>
        <div style={{textAlign: 'center', marginBottom: '40px'}}>
          <h2 style={{
            fontSize: '28px',
            fontWeight: '700',
            color: '#1f2937',
            marginBottom: '12px',
            lineHeight: '1.2'
          }}>
            Add New Item
          </h2>
          <p style={{
            color: '#6b7280',
            fontSize: '16px',
            lineHeight: '1.5',
            margin: '0 0 20px 0'
          }}>
            Record equipment details to add to your inventory
          </p>

          {/* Sample Text Button */}
          <button
            onClick={() => {
              const sampleText = "This is a Canon camera, the R5 model I think. Got it from some dealer last year. Condition looks good, working fine but has some scratches on the body. Don't know the exact serial number... will check later. We keep it somewhere in the back warehouse. Pretty expensive camera, should rent for good money.";
              console.log('Using sample text:', sampleText);
              // Simulate the processing with sample data
              setPageState('processing');
              setTimeout(() => {
                setExtractedData({
                  name: "Canon EOS R5",
                  brand: "Canon",
                  model: "R5", 
                  category: "cameras",
                  description: "Camera from Canon, R5 model",
                  serial_number: "",
                  condition: "good",
                  purchase_price: 0,
                  current_value: 0,
                  price_per_day: 0,
                  location: "back warehouse",
                  notes: "Has some scratches on body but working fine",
                  specifications: {}
                });
                setPageState('form');
              }, 3000);
            }}
            style={{
              backgroundColor: '#f3f4f6',
              border: '1px solid #d1d5db',
              borderRadius: '8px',
              padding: '8px 16px',
              fontSize: '14px',
              color: '#374151',
              cursor: 'pointer',
              marginBottom: '20px'
            }}
          >
            ðŸ§ª Use Sample Data (for testing)
          </button>

          {/* Speaking Prompts */}
          <div style={{textAlign: 'left', marginBottom: '20px'}}>
            <p style={{fontSize: '14px', fontWeight: '600', color: '#374151', marginBottom: '8px'}}>
              ðŸ’¡ What to mention when recording:
            </p>
            <div style={{display: 'flex', flexWrap: 'wrap', gap: '8px', justifyContent: 'center'}}>
              {[
                'Equipment name & brand',
                'Model number', 
                'Serial number',
                'Condition',
                'Purchase price',
                'Current value',
                'Daily rate',
                'Storage location',
                'Any issues or notes'
              ].map((prompt, index) => (
                <span key={index} style={{
                  backgroundColor: '#fef3c7',
                  color: '#92400e',
                  padding: '4px 8px',
                  borderRadius: '12px',
                  fontSize: '12px',
                  border: '1px solid #fbbf24'
                }}>
                  {prompt}
                </span>
              ))}
            </div>
          </div>
        </div>

        <div style={{
          backgroundColor: '#f8fafc',
          borderRadius: '24px',
          padding: '32px 24px',
          boxShadow: '0 4px 24px rgba(0, 0, 0, 0.06)',
          border: '1px solid #e2e8f0'
        }}>
          <VoiceRecorder
            onRecordingComplete={handleRecordingComplete}
            onCancel={() => navigate('/inventory')}
          />
        </div>
      </div>

      <ProcessingModal
        isOpen={pageState === 'processing'}
        onCancel={handleProcessingCancel}
        onComplete={handleProcessingComplete}
        audioBlob={audioBlob}
      />
    </>
  );
};

export default AddItemPage;
