import React from 'react';
import { Outlet } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { SignOut } from 'phosphor-react';
import BottomTabBar from './BottomTabBar';

const Layout: React.FC = () => {
  const { logout } = useAuth();

  console.log('Layout component rendering');

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header */}
      <header className="flex justify-between items-center p-4 border-b border-gray-light bg-background sticky top-0 z-10">
        <h1 className="font-heading font-bold text-primary text-xl">
          Camorent Inventory
        </h1>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 text-gray-medium hover:text-primary transition-colors p-2 min-h-[44px]"
          title="Logout"
        >
          <SignOut size={20} />
          <span className="text-sm">Logout</span>
        </button>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-auto pb-20">
        <div className="mobile-container">
          <Outlet />
        </div>
      </main>

      {/* Bottom Tab Bar */}
      <BottomTabBar />
    </div>
  );
};

export default Layout;
