import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../utils/api';
import VoiceRecorder from '../components/VoiceRecorder';
import ProcessingModal from '../components/ProcessingModal';
import EquipmentForm from '../components/EquipmentForm';

type PageState = 'recording' | 'processing' | 'form';

const AddItemPage: React.FC = () => {
  const [pageState, setPageState] = useState<PageState>('recording');
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const [extractedData, setExtractedData] = useState<any>(null);
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  const handleRecordingComplete = (blob: Blob) => {
    setAudioBlob(blob);
    setPageState('processing');
  };

  const handleProcessingComplete = (data: any) => {
    setExtractedData(data);
    setPageState('form');
  };

  const handleProcessingCancel = () => {
    setPageState('recording');
    setAudioBlob(null);
  };

  const handleFormSubmit = async (formData: any) => {
    setSaving(true);
    try {
      const { user } = useAuth();
      if (!user) {
        throw new Error('User not authenticated');
      }

      // First, create or find the SKU
      const skuData = {
        name: formData.name,
        brand: formData.brand,
        model: formData.model,
        category: formData.category,
        description: formData.description,
        specifications: formData.specifications,
        price_per_day: formData.price_per_day,
        security_deposit: formData.security_deposit
      };

      const skuResult = await api.createSKU(skuData);

      if (!skuResult.success) {
        throw new Error(skuResult.error || 'Failed to create SKU');
      }

      const skuId = skuResult.data?.id;

      // Then, create the inventory item (always available for initial digitalization)
      const inventoryData = {
        sku_id: skuId,
        serial_number: formData.serial_number,
        barcode: formData.barcode,
        condition: formData.condition,
        status: formData.status,
        location: formData.location,
        purchase_price: formData.purchase_price,
        current_value: formData.current_value,
        notes: formData.notes,
        created_by: user.uid
      };

      const inventoryResult = await api.createInventoryItem(inventoryData);

      if (!inventoryResult.success) {
        throw new Error(inventoryResult.error || 'Failed to create inventory item');
      }

      // Navigate to inventory page after successful save
      navigate('/inventory');
    } catch (error) {
      console.error('Error saving equipment:', error);
      alert('Failed to save equipment. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  const handleFormCancel = () => {
    setPageState('recording');
    setAudioBlob(null);
    setExtractedData(null);
  };

  if (pageState === 'form') {
    return (
      <div className="p-6">
        <div className="text-center mb-8">
          <h2 className="font-heading font-bold text-primary text-2xl mb-2">
            Review & Save Equipment
          </h2>
          <p className="text-gray-medium">
            Verify details and add to digital inventory
          </p>
        </div>

        <EquipmentForm
          initialData={extractedData}
          confidenceScores={extractedData?.confidence_scores}
          onSubmit={handleFormSubmit}
          onCancel={handleFormCancel}
          loading={saving}
        />
      </div>
    );
  }

  return (
    <>
      <div style={{padding: '0', minHeight: '400px'}}>
        <div style={{textAlign: 'center', marginBottom: '40px'}}>
          <h2 style={{
            fontSize: '28px',
            fontWeight: '700',
            color: '#1f2937',
            marginBottom: '12px',
            lineHeight: '1.2'
          }}>
            Add New Item
          </h2>
          <p style={{
            color: '#6b7280',
            fontSize: '16px',
            lineHeight: '1.5',
            margin: '0 0 20px 0'
          }}>
            Record equipment details to add to your inventory
          </p>

          {/* Sample Text Button */}
          <button
            onClick={() => {
              const sampleText = "Arre yaar, this is that Canon camera na... the R5 model. Boss bought it last year for some 2.5 lakh I think. Condition is quite good actually, just few scratches here and there but working perfectly fine. Serial number... let me check... haan CAN123456. We keep it in warehouse section A, shelf number 3. Market mein now it's going for around 2.2 lakh maybe. For rental we charge 2000 per day.";
              console.log('Using sample text:', sampleText);
              // Simulate the processing with sample data
              setPageState('processing');
              setTimeout(() => {
                setExtractedData({
                  name: "Canon EOS R5",
                  brand: "Canon",
                  model: "EOS R5", 
                  category: "cameras",
                  description: "Professional mirrorless camera with 45 megapixel resolution",
                  serial_number: "CAN123456",
                  condition: "good",
                  purchase_price: 250000,
                  current_value: 220000,
                  price_per_day: 2000,
                  location: "Warehouse section A, shelf 3",
                  notes: "Some minor scratches on body but works perfectly",
                  specifications: {
                    resolution: "45 megapixels",
                    type: "Mirrorless",
                    mount: "Canon RF",
                    video: "8K recording",
                    iso_range: "100-51200"
                  }
                });
                setPageState('form');
              }, 3000);
            }}
            style={{
              backgroundColor: '#f3f4f6',
              border: '1px solid #d1d5db',
              borderRadius: '8px',
              padding: '8px 16px',
              fontSize: '14px',
              color: '#374151',
              cursor: 'pointer',
              marginBottom: '20px'
            }}
          >
            🧪 Use Sample Data (for testing)
          </button>

          {/* Speaking Prompts */}
          <div style={{textAlign: 'left', marginBottom: '20px'}}>
            <p style={{fontSize: '14px', fontWeight: '600', color: '#374151', marginBottom: '8px'}}>
              💡 What to mention when recording:
            </p>
            <div style={{display: 'flex', flexWrap: 'wrap', gap: '8px', justifyContent: 'center'}}>
              {[
                'Equipment name & brand',
                'Model number', 
                'Serial number',
                'Condition',
                'Purchase price',
                'Current value',
                'Daily rate',
                'Storage location',
                'Any issues or notes'
              ].map((prompt, index) => (
                <span key={index} style={{
                  backgroundColor: '#fef3c7',
                  color: '#92400e',
                  padding: '4px 8px',
                  borderRadius: '12px',
                  fontSize: '12px',
                  border: '1px solid #fbbf24'
                }}>
                  {prompt}
                </span>
              ))}
            </div>
          </div>
        </div>

        <div style={{
          backgroundColor: '#f8fafc',
          borderRadius: '24px',
          padding: '32px 24px',
          boxShadow: '0 4px 24px rgba(0, 0, 0, 0.06)',
          border: '1px solid #e2e8f0'
        }}>
          <VoiceRecorder
            onRecordingComplete={handleRecordingComplete}
            onCancel={() => navigate('/inventory')}
          />
        </div>
      </div>

      <ProcessingModal
        isOpen={pageState === 'processing'}
        onCancel={handleProcessingCancel}
        onComplete={handleProcessingComplete}
        audioBlob={audioBlob}
      />
    </>
  );
};

export default AddItemPage;
